// Get raw text from chatInput
const rawText = $input.first().json.content;
const lines = rawText.split("\n");

// Function to parse bold and links
const createRichText = (text) => {
  const linkRegex = /\[([^\]]+)\]\(([^)]+)\)/g;
  const boldRegex = /\*\*(.*?)\*\*/g;
  let result = [];
  let lastIndex = 0;
  let match;

  // أولا: روابط
  while ((match = linkRegex.exec(text)) !== null) {
    // أي نص قبل الرابط
    if (match.index > lastIndex) {
      result.push(...parseBold(text.substring(lastIndex, match.index)));
    }
    // إضافة الرابط كـ Anchor
    result.push({
      type: "text",
      text: { content: match[1], link: { url: match[2] } },
      annotations: { bold: false, italic: false, strikethrough: false, underline: false, code: false, color: "default" },
      plain_text: match[1],
      href: match[2]
    });
    lastIndex = match.index + match[0].length;
  }
  // الباقي بعد آخر رابط
  if (lastIndex < text.length) {
    result.push(...parseBold(text.substring(lastIndex)));
  }
  return result.length ? result : [{ type: "text", text: { content: text }, annotations: { bold: false, italic: false, strikethrough: false, underline: false, code: false, color: "default" }, plain_text: text, href: null }];
};

// دالة bold فقط (تُستخدم داخل الدالة أعلاه)
function parseBold(text) {
  const boldRegex = /\*\*(.*?)\*\*/g;
  let parts = [];
  let lastIndex = 0;
  let match;
  while ((match = boldRegex.exec(text)) !== null) {
    if (match.index > lastIndex) {
      parts.push({
        type: "text",
        text: { content: text.substring(lastIndex, match.index) },
        annotations: { bold: false, italic: false, strikethrough: false, underline: false, code: false, color: "default" },
        plain_text: text.substring(lastIndex, match.index),
        href: null
      });
    }
    parts.push({
      type: "text",
      text: { content: match[1] },
      annotations: { bold: true, italic: false, strikethrough: false, underline: false, code: false, color: "default" },
      plain_text: match[1],
      href: null
    });
    lastIndex = match.index + match[0].length;
  }
  if (lastIndex < text.length) {
    parts.push({
      type: "text",
      text: { content: text.substring(lastIndex) },
      annotations: { bold: false, italic: false, strikethrough: false, underline: false, code: false, color: "default" },
      plain_text: text.substring(lastIndex),
      href: null
    });
  }
  return parts.length ? parts : [{ type: "text", text: { content: text }, annotations: { bold: false, italic: false, strikethrough: false, underline: false, code: false, color: "default" }, plain_text: text, href: null }];
}

// Block constructors
const createHeading = (text, level = 1) => ({
  object: "block",
  type: `heading_${level}`,
  [`heading_${level}`]: { rich_text: createRichText(text) }
});

const createParagraph = (text) => ({
  object: "block",
  type: "paragraph",
  paragraph: { rich_text: createRichText(text) }
});

const createBulletListItem = (text) => ({
  object: "block",
  type: "bulleted_list_item",
  bulleted_list_item: { rich_text: createRichText(text) }
});

const createNumberedListItem = (text) => {
  let cleanedText = text.replace(/^\d+\.\s*/, "").trim();
  return {
    object: "block",
    type: "numbered_list_item",
    numbered_list_item: { rich_text: createRichText(cleanedText) }
  };
};

// Main parsing
let blocks = [];
let paragraphContent = [];

for (let line of lines) {
  line = line.trim();

  if (line.startsWith("# ")) {
    if (paragraphContent.length > 0) {
      blocks.push(createParagraph(paragraphContent.join("\n\n")));
      paragraphContent = [];
    }
    blocks.push(createHeading(line.replace("# ", ""), 1));
  }
  else if (line.startsWith("## ")) {
    if (paragraphContent.length > 0) {
      blocks.push(createParagraph(paragraphContent.join("\n\n")));
      paragraphContent = [];
    }
    blocks.push(createHeading(line.replace("## ", ""), 2));
  }
  else if (line.startsWith("### ")) {
    if (paragraphContent.length > 0) {
      blocks.push(createParagraph(paragraphContent.join("\n\n")));
      paragraphContent = [];
    }
    blocks.push(createHeading(line.replace("### ", ""), 3));
  }
  else if (line.match(/^\d+\./)) {
    if (paragraphContent.length > 0) {
      blocks.push(createParagraph(paragraphContent.join("\n\n")));
      paragraphContent = [];
    }
    blocks.push(createNumberedListItem(line));
  }
  else if (line.startsWith("- ")) {
    if (paragraphContent.length > 0) {
      blocks.push(createParagraph(paragraphContent.join("\n\n")));
      paragraphContent = [];
    }
    blocks.push(createBulletListItem(line.replace("- ", "").trim()));
  }
  else if (line.length > 0) {
    paragraphContent.push(line);
  }
  else if (line.length === 0 && paragraphContent.length > 0) {
    blocks.push(createParagraph(paragraphContent.join("\n\n")));
    paragraphContent = [];
  }
}

// في نهاية الملف: إذا فيه محتوى متراكم لم يُدفع بعد
if (paragraphContent.length > 0) {
  blocks.push(createParagraph(paragraphContent.join("\n\n")));
}

// **النقطة المهمة:**
// في n8n يجب أن يكون الخرج مصفوفة من الكائنات، كل كائن يُمثل بند (block)
// مثال: [{json: {...}}, {json: {...}}, ...]
return [{ json: { children: blocks } }];

const items = $json.tasks[0].result[0].items;
const paa = items.find(item => item.type === 'people_also_ask');

// تأكد من وجود البيانات
if (!paa || !paa.items) {
  return [];
}

// ضُم السؤال والإجابة في String واحد
return paa.items.map(el => {
  const question = el.title || "";
  const answer = (el.expanded_element && el.expanded_element[0]) ? el.expanded_element[0].description : "";
  return { 
    FAQ: `سؤال: ${question} | إجابة: ${answer}` 
  };
});

