// 从输入数据中获取第一个 item 的 content 字段
// 注意：根据您的输入结构，可能需要调整 '$input.first().json.content'
// 例如，如果 Aggregate1 的输出是一个数组，而您要处理的是数组的第一个元素
// 并且 Aggregate1 本身已经把 content 暴露出来了。
// 假设 Aggregate1 的输出是 [{ json: { content: "..." } }]
// 如果 Aggregate1 的输出是 [{ content: "..." }]，则需要调整为 $input.first().content
// 从图片上看，Aggregate1 的输出是 { json: { content: "..." } } 格式
const rawText = $input.first().json.content;


if (!rawText) {
  // 如果没有 content 字段，返回符合格式的空输出
  return [{ json: { content: "", raw: "" } }]; // 返回一个包含原始键的对象
}

// 1. 替换 \n 为实际的换行符
let plainText = rawText.replace(/\\n/g, '\n');

// 2. 移除 Markdown 标记

// 移除标题标记 (#, ##, ###)
plainText = plainText.replace(/^#\s+/gm, '');
plainText = plainText.replace(/^##\s+/gm, '');
plainText = plainText.replace(/^###\s+/gm, '');

// 移除列表标记 (- )
plainText = plainText.replace(/^- /gm, '');

// 移除链接标记 [text](url) - 只保留 text
plainText = plainText.replace(/\[([^\]]+)\]\([^\)]+\)/g, '$1');

// 移除粗体标记 **text** - 只保留 text
plainText = plainText.replace(/\*\*(.*?)\*\*/g, '$1');

// (可选) 移除其他 Markdown 符号，例如斜体 (*) - 如果您的输入中可能包含
// plainText = plainText.replace(/\*(.*?)\*/g, '$1');

// (可选) 进一步处理换行符，例如将连续的空行合并为一个，或者移除开头/结尾的空白
// 移除行首/行尾的所有空白字符 (包括换行符)
plainText = plainText.trim();
// 将连续的换行符替换为一个换行符，避免段落之间出现过多空白
// 这个正则匹配两个或更多的换行符
plainText = plainText.replace(/\n{2,}/g, '\n');


// **修复错误：** 将 plainText 包装在一个对象中，作为 json 属性的值
return [{ json: { cleanContent: plainText } }];
