// 处理RSS Feed返回的数据
const results = [];
const existingTitles = new Set();

// 检查是否有数据
if (!items || items.length === 0) {
  return [];
}

// 遍历所有RSS条目
for (const item of items) {
  // 提取文章信息
  const title = item.json.title || '';
  const description = item.json.content || item.json.contentSnippet || '';
  const link = item.json.link || '';
  const pubDate = item.json.pubDate || item.json.isoDate || new Date().toISOString();
  const source = item.json.source?.name || 'Technology Review';

  // 检查是否与AI相关
  const titleLower = title.toLowerCase();
  const descLower = description.toLowerCase();
  const aiKeywords = ['ai', 'artificial intelligence', '人工智能', '大模型', 'llm', 'chatgpt', 'gpt'];

  const isAIRelated = aiKeywords.some(keyword => 
    titleLower.includes(keyword) || descLower.includes(keyword));

  // 如果与AI相关且未重复，添加到结果中
  if (isAIRelated && !existingTitles.has(title)) {
    existingTitles.add(title);

    // 提取所有标签并存储在一个数组中
    const tags = [];

    // 从RSS分类中提取标签
    if (item.json.categories && Array.isArray(item.json.categories)) {
      for (const category of item.json.categories) {
        // 避免添加重复标签
        if (!tags.some(tag => tag.name === category)) {
          tags.push({ name: category });
        }
      }
    }

    // 基于内容关键词添加额外标签
    const tagKeywords = [
      { keyword: 'chatgpt', tag: 'ChatGPT' },
      { keyword: 'gpt', tag: 'GPT' },
      { keyword: 'llm', tag: '大模型' },
      { keyword: '大模型', tag: '大模型' },
      { keyword: 'artificial intelligence', tag: 'Artificial Intelligence' },
      { keyword: '人工智能', tag: '人工智能' },
      { keyword: 'algorithm', tag: 'The Algorithm' },
      { keyword: '算法', tag: '算法' },
      { keyword: 'machine learning', tag: '机器学习' },
      { keyword: '机器学习', tag: '机器学习' }
    ];

    for (const { keyword, tag } of tagKeywords) {
      if ((titleLower.includes(keyword) || descLower.includes(keyword)) && 
          !tags.some(t => t.name === tag)) {
        tags.push({ name: tag });
      }
    }

    // 如果没有标签，添加默认标签
    if (tags.length === 0) {
      tags.push({ name: 'AI' });
    }

    // 创建Notion数据库条目格式
    results.push({
      json: {
        properties: {
          '标题': {
            title: [{ text: { content: title } }]
          },
          '内容': {
            rich_text: [{ text: { content: description.substring(0, 2000) } }] // Notion限制rich_text长度
          },
          '来源': {
            select: { name: source }
          },
          '发布日期': {
            date: { start: pubDate }
          },
          '链接': {
            url: link
          },
          '标签': {
            multi_select: tags // 作为一个完整的数组传递
          }
        }
      }
    });
  }
}

return results;
